import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class FileUploadService {

  // âœ… n8n Webhook endpoint
  private n8nWebhookUrl = 'https://exceptionally-nonjudiciable-marvin.ngrok-free.dev/webhook-test/e314dcf5-07c6-4ff0-bc86-609687c69c2c';

  constructor(private http: HttpClient) {}

  /**
   * Sends the uploaded file (PDF) to n8n via webhook.
   * n8n receives it as multipart/form-data.
   */
  uploadToN8n(file: File, createdBy: string, type: string): Observable<any> {
    const formData = new FormData();

    // Add the file
    formData.append('file', file, file.name);

    // Optional metadata
    formData.append('createdby', createdBy);
    formData.append('type', type);

    // Send POST to n8n webhook
    return this.http.post(this.n8nWebhookUrl, formData);
  }
}
